require('p5');
var ColorHash = require('color-hash');
var CH = new ColorHash();

function setup() {
    var canvas = createCanvas(1200, 800);
    canvas.parent('canvas_view');
    // drawingContext.globalCompositeOperation = "lighter";
    textSize(20);
    noStroke();
    noLoop();
}

function draw() {
    for (var i = 0; i < polygons.length; i ++){
        if(tag[i] == -1){
            continue;
        }
        var colorHash = new ColorHash();
        fill('rgba('+colorHash.rgb(tag[i])+',0.5)');
        polygon = polygons[i];
        push();
        beginShape();
        for (var j = 0; j < polygon.length; j ++){
            [x, y] = coordtransform.wgs84togcj02(polygon[j][0],polygon[j][1]);
            x = (x- WEST) / EASTWEST * WIDTH;
            y = (NORTH - y) / NORTHSOUTH * HEIGHT;
            vertex(x,y);
        }
        endShape(CLOSE);
        pop();
    }
}

