GoogleMapsLoader.KEY = 'AIzaSyA676X6MxO6WGnm7CTBCgMq7j7ok-8-5wY';
var MAP_STYLE = require('./map_style.json');

var coordtransform = require('coordtransform');
var gcj_center = coordtransform.wgs84togcj02(CENTER[1], CENTER[0]);

function init_map() {
    GoogleMapsLoader.load(function(google) {
        var map = new google.maps.Map(document.getElementById('map_view'), {
            center: {
                lat: gcj_center[1],
                lng: gcj_center[0]
            },
            zoom: ZOOM,
            styles: MAP_STYLE,
            disableDefaultUI: true
        });
        map.addListener('bounds_changed', function() {
            var ne = map.getBounds().getNorthEast();
            var sw = map.getBounds().getSouthWest();
            console.log('Bounds:', ne.lat(), ne.lng(), sw.lat(), sw.lng());
            NORTH = ne.lat(), EAST = ne.lng(), WEST = sw.lng(), SOUTH = sw.lat();
            [WEST, SOUTH] = [sw.lng(), sw.lat()];
            [EAST, NORTH] = [ne.lng(), ne.lat()];
            EASTWEST = EAST - WEST, NORTHSOUTH = NORTH - SOUTH;
        });
    });
}